import React from "react";
import Question from "./components/Question";
import { nanoid } from "nanoid";



function App() {

  const [allQuestions, setallQuestions] = React.useState([])

     React.useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&difficulty=medium&type=multiple")
        .then(res => res.json())
        .then(results => setallQuestions(results.results))
    }, [])

    const answers = allQuestions.map(question => question.incorrect_answers.map(answer => <div className="answer" key={nanoid()}>{answer}</div>))

    const questionEl = allQuestions.map(question => <Question
        key={nanoid()}
        question={question.question}
        correctAnswer={question.correct_answer}
        incorrectAnswer={answers}
      />)



  return (
    <div className="App">
      {questionEl}
    </div>
  );
}

export default App;
