import React from "react";
import Question from "./components/Question";
import { nanoid } from "nanoid";

function App() {

  const [allQuestions, setallQuestions] = React.useState([])


     React.useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&difficulty=medium&type=multiple")
        .then(res => res.json())
        .then(results => setallQuestions(results.results))
    }, [])

    function generateStatus(){
      setallQuestions(oldQuestion => {return{
            ...oldQuestion,
            isHeld: false
          }})
    }

    // function allNewQuestions(){
    //   const newQuestions = [];
    //   const newQuestion = setallQuestions(oldQuestion => {return{
    //     ...oldQuestion,
    //     isHeld: false
    //   }})
    //   for (let i = 0; i < allQuestions.length; i++){
    //     newQuestions.push(newQuestion())
    //   }
    //   return newQuestions;
    // }
    // console.log(allNewQuestions())

    function shuffleAnswers(answers) {
      for (let i = answers.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [answers[i], answers[j]] = [answers[j], answers[i]];
      }
      return answers;
    }

    const questionEl = allQuestions.map(question => {
      const answers = shuffleAnswers([question.correct_answer, ...question.incorrect_answers]);
      return (
        <Question
          key={nanoid()}
          question={question.question}
          answers={answers}
        />
      );
    });


  return (
    <div className="App">
      {questionEl}
      <button className="answer-button">Check answers</button>
    </div>
  );
}

export default App;
